const items = $input.all();
let output = 'Negocia√ß√£o\nVendedor\n';

for (const item of items) {
  const row = item.json;

  // Corrigindo o nome da chave com espa√ßo no final
  const nome = (row["Negocia√ß√£o "] || '').trim();

  // Ignora a linha de cabe√ßalho
  if (nome.toLowerCase() === 'vendedor') continue;

  // Garante que os valores s√£o n√∫meros
  const col2 = parseInt(row.col_2 ?? 0);
  const col3 = parseInt(row.col_3 ?? 0);
  const col4 = parseInt(row.col_4 ?? 0);

  const frase = `${nome} 11:00 quantidade de ${col2} ${col2 === 1 ? 'negocia√ß√£o' : 'negocia√ß√µes'}; ` +
                `15:00 quantidade de ${col3} ${col3 === 1 ? 'negocia√ß√£o' : 'negocia√ß√µes'}; ` +
                `17:30 quantidade de ${col4} ${col4 === 1 ? 'negocia√ß√£o' : 'negocia√ß√µes'}.`;

  output += frase + '\n';
}

return [
  {
    json: {
      atividade: 'Negocia√ß√£o',
      dados: output.trim()
    }
  }
];

// Fun√ß√£o para extrair quantidade de uma frase como "15:00 quantidade de 4 liga√ß√µes"
function extrairQuantidade(texto) {
  const match = texto.match(/quantidade de\s*(\d+)/);
  return match ? parseInt(match[1]) : 0;
}

// Objeto final com os dados unificados
const consolidado = {};

// Iterar sobre cada item (atividade)
for (const item of items) {
  const atividade = item.json.atividade.toLowerCase();
  const linhas = item.json.dados.split('\n').slice(2); // ignora cabe√ßalhos

  for (const linha of linhas) {
    if (!linha.trim()) continue;

    const partes = linha.split(/(?=\d{1,2}:\d{2}\s+quantidade)/g);
    const nome = partes[0].split(' ')[0];

    if (!consolidado[nome]) {
      consolidado[nome] = {
        nome,
        ligacoes: 0,
        prospeccoes: 0,
        negociacoes: 0
      };
    }

    for (const parte of partes) {
      const qtd = extrairQuantidade(parte);
      if (atividade.includes("negocia√ß√£o")) {
        consolidado[nome].negociacoes += qtd;
      } else if (atividade.includes("prospec")) {
        consolidado[nome].prospeccoes += qtd;
      } else if (atividade.includes("liga√ß√£o") || atividade.includes("liga√ß√µes")) {
        consolidado[nome].ligacoes += qtd;
      }
    }
  }
}

// Retornar em um √∫nico item com todos os dados juntos
return [
  {
    json: {
      dados: Object.values(consolidado)
    }
  }
];
